def import_coordinates(abundance_df, meta_df) :
    coordinates = meta_df[['uuid', 'longitude', 'latitude']].copy()  # Copying the slice to a new DataFrame
    coordinates['uuid'] = coordinates['uuid'].astype(str)    
    abundance_df.loc[:, 'uuid'] = abundance_df['uuid'].astype(str)
    coordinates['uuid'] = coordinates['uuid'].astype(str)
    df = pd.merge(abundance_df, coordinates, on='uuid', how="inner")
    df = df.dropna()
    return df

def import_uuid(Filtered_predictors_on_vif, predictors) :
    # Assuming predictors is derived from another DataFrame, add .copy() to ensure it's a standalone copy.
    predictors = Filtered_predictors_on_vif["uuid"].copy()

    # Now, when you use .loc to set values, it should not raise the warning.
    predictors.loc[:, "uuid"] = Filtered_predictors_on_vif["uuid"]

    return predictors

def extract_response_variables(df) : 
    columns = [col for col in df.columns if col in ['longitude', 'latitude']]
    df['longitude'] = pd.to_numeric(df['longitude'], errors='coerce')
    df['latitude'] = pd.to_numeric(df['latitude'], errors='coerce')

    return df[columns]

